mapper.select:
SELECT APP.ID AS approvalId, APP.APPROVAL_TYPE_CODE AS apprTypeCode, TYPE_CODE.NAME AS apprTypeName, REQ_EMP.NAME AS requesterName, APP.REQUESTER_ID AS requesterId, APP.REQUESTED_AT AS requested_at, REQ_EMP.POSITION_CODE AS positionCode, POS_CODE.NAME AS positionName, REQ_EMP.DEPT_CODE AS deptCode, DEPT_CODE.NAME AS deptName, APP.CONFIRMED_AT AS confirmedDate, APP.APPROVAL_STATUS_CODE AS approvalStatusCode, STATUS_CODE.NAME AS approvalStatusName, APRV_EMP.NAME AS approverName, APP.APPROVER_ID AS approverId, APP.REJECT_REASON AS rejectionReason FROM APPROVAL APP LEFT JOIN EMPLOYEE REQ_EMP ON REQ_EMP.ID = APP.REQUESTER_ID LEFT JOIN EMPLOYEE APRV_EMP ON APRV_EMP.ID = APP.APPROVER_ID LEFT JOIN DETAIL_SYS_CODE STATUS_CODE ON APP.APPROVAL_STATUS_CODE = STATUS_CODE.DETAIL_CODE LEFT JOIN DETAIL_SYS_CODE TYPE_CODE ON APP.APPROVAL_TYPE_CODE = TYPE_CODE.DETAIL_CODE LEFT JOIN DETAIL_SYS_CODE POS_CODE ON REQ_EMP.POSITION_CODE = POS_CODE.DETAIL_CODE LEFT JOIN DETAIL_SYS_CODE DEPT_CODE ON REQ_EMP.DEPT_CODE = DEPT_CODE.DETAIL_CODE
AND APP.REQUESTED_AT >= #{startDate}
AND APP.REQUESTED_AT <= #{endDate}
AND APP.APPROVAL_TYPE_CODE = #{apprType}
AND REQ_EMP.DEPT_CODE = #{dept}
AND REQ_EMP.POSITION_CODE = #{position}
AND ( REQ_EMP.NAME LIKE CONCAT(CONCAT('%', #{approvalNameOrEmpId}), '%') OR TO_CHAR(REQ_EMP.ID) LIKE CONCAT(CONCAT('%', #{approvalNameOrEmpId}), '%') )
ORDER BY APP.ID DESC
