mapper.select:

SELECT CASE WHEN COUNT(*) > 0 THEN 1 ELSE 0 END
FROM vacation_history vh
         JOIN approval a ON vh.approval_id = a.id
WHERE vh.employee_id = #{empId}
  AND a.approval_status_code != 'APV003'
AND (
    (vh.start_date BETWEEN #{startTime}
  AND #{endTime})
   OR
    (vh.end_date BETWEEN #{startTime}
  AND #{endTime})
   OR
    (vh.start_date <= #{startTime}
  AND vh.end_date >= #{endTime})
    )
